export class UpdateTaskUseCase {
  constructor(private taskRepository: TaskRepository) {}

  async execute(taskId: string, data: UpdateTaskDto) {
    const taskEntity = UpdateTaskAdapter.toEntity(data);
    return this.taskRepository.update(taskId, taskEntity);
  }
}

// src/task/controllers/task.controller.ts
