  constructor(private updateTaskUseCase: UpdateTaskUseCase) {}

  @Patch('/:id')
  async updateTask(
    @Param('id') taskId: string,
    @Body() payload: UpdateTaskDto
  ) {
    return this.updateTaskUseCase.execute(taskId, payload);
  }
}
