+/**
+ * Validates a JWT token and returns the decoded user data
+ * 
+ * @param token - The JWT token to validate
+ * @returns The decoded user data from the token
+ * @throws {jwt.JsonWebTokenError} If token is invalid
+ * @throws {jwt.TokenExpiredError} If token has expired
+ * @throws {jwt.NotBeforeError} If token is not active yet
+ */
+export const validateToken = (token: string): any => {
+  try {
+    const decoded = jwt.verify(token, config.jwtSecret);
+    return decoded;
+  } catch (error) {
+    // Re-throw the error to be handled by the caller
+    throw error;
+  }
+};
