+import { Request, Response, NextFunction } from 'express';
+import jwt from 'jsonwebtoken';
+import { PrismaClient } from '@prisma/client';
+import { AuthError } from '../errors/auth-error';
+
+const prisma = new PrismaClient();
+
+// Extender la interfaz Request para incluir el usuario
+declare global {
+  namespace Express {
+    interface Request {
+      user?: {
+        id: string;
+        email: string;
+        role: string;
+      };
+    }
+  }
+}
+
+/**
+ * Middleware para verificar la autenticaciÃ³n del usuario mediante JWT
+ * 
+ * @param {Request} req - Objeto de solicitud Express
+ * @param {Response} res - Objeto de respuesta Express
+ * @param {NextFunction} next - FunciÃ³n para continuar al siguiente middleware
+ */
+export const authenticate = async (req: Request, res: Response, next: NextFunction) => {
+  try {
+    const authHeader = req.headers.authorization;
+    
+    if (!authHeader || !authHeader.startsWith('Bearer ')) {
+      throw new AuthError('Token no proporcionado', 401);
+    }
+    
+    const token = authHeader.split(' ')[1];
+    
+    // Verificar el token
+    const decoded = jwt.verify(token, process.env.JWT_SECRET as string) as { userId: string };
+    
+    // Buscar el usuario en la base de datos
+    const user = await prisma.user.findUnique({
+      where: { id: decoded.userId },
+      select: { id: true, email: true, role: true }
+    });
+    
+    if (!user) {
+      throw new AuthError('Usuario no encontrado', 404);
+    }
+    
+    // AÃ±adir el usuario a la solicitud para uso posterior
+    req.user = user;
+    next();
+  } catch (error) {
+    if (error instanceof jwt.JsonWebTokenError) {
+      next(new AuthError('Token invÃ¡lido', 401));
+    } else {
+      next(error);
+    }
+  }
+};","note":"Crear un middleware de autenticaciÃ³n reutilizable con tipos TypeScript para mejorar la DX"},{"path":"src/errors/auth-error.ts","patch":"--- a/src/errors/auth-error.ts
