--- a/tests/e2e/change-request.test.ts
+++ b/tests/e2e/change-request.test.ts
@@ -0,0 +1,89 @@
+import { describe, it, expect, beforeAll, afterAll } from 'vitest';
+import request from 'supertest';
+import { app } from '../../src/app';
+import { PrismaClient } from '@prisma/client';
+import { createTestUser, cleanupTestUser } from '../helpers/test-user-helper';
+
+describe('Change Request E2E', () => {
+  const prisma = new PrismaClient();
+  let authToken: string;
+  let userId: string;
+  let changeRequestId: string;
+
+  beforeAll(async () => {
+    // Setup test user and get auth token
+    const testUser = await createTestUser(prisma);
+    userId = testUser.id;
+    
+    const loginResponse = await request(app)
+      .post('/api/auth/login')
+      .send({
+        email: testUser.email,
+        password: 'Test@123'
+      });
+    
+    authToken = loginResponse.body.token;
+  });
+
+  afterAll(async () => {
+    // Cleanup test data
+    await prisma.changeRequest.deleteMany({
+      where: {
+        createdById: userId
+      }
+    });
+    
+    await cleanupTestUser(prisma, userId);
+    await prisma.$disconnect();
+  });
+
+  it('should create a new change request', async () => {
+    const response = await request(app)
+      .post('/api/change-requests')
+      .set('Authorization', `Bearer ${authToken}`)
+      .send({
+        title: 'Test Change Request',
+        description: 'This is a test change request for E2E testing',
+        targetEntity: 'PRODUCT',
+        targetId: 'mock-product-id',
+        changes: {
+          name: 'Updated Product Name',
+          price: 29.99
+        }
+      });
+
+    expect(response.status).toBe(201);
+    expect(response.body).toHaveProperty('id');
+    expect(response.body.title).toBe('Test Change Request');
+    expect(response.body.status).toBe('PENDING');
+    
+    changeRequestId = response.body.id;
+  });
+
+  it('should get a change request by id', async () => {
+    const response = await request(app)
+      .get(`/api/change-requests/${changeRequestId}`)
+      .set('Authorization', `Bearer ${authToken}`);
+
+    expect(response.status).toBe(200);
+    expect(response.body.id).toBe(changeRequestId);
+    expect(response.body.title).toBe('Test Change Request');
+  });
+
+  it('should update a change request status', async () => {
+    const response = await request(app)
+      .patch(`/api/change-requests/${changeRequestId}/status`)
+      .set('Authorization', `Bearer ${authToken}`)
+      .send({
+        status: 'APPROVED',
+        comment: 'Approved in E2E test'
+      });
+
+    expect(response.status).toBe(200);
+    expect(response.body.status).toBe('APPROVED');
+    expect(response.body.statusHistory).toHaveLength(2); // Initial PENDING + new APPROVED
+    expect(response.body.statusHistory[1].comment).toBe('Approved in E2E test');
+  });
+});

