+import { PrismaClient, User } from '@prisma/client';
+import bcrypt from 'bcrypt';
+import { v4 as uuidv4 } from 'uuid';
+
+/**
+ * Creates a test user for E2E testing
+ */
+export async function createTestUser(prisma: PrismaClient): Promise<User> {
+  const hashedPassword = await bcrypt.hash('Test@123', 10);
+  const email = `test-${uuidv4()}@yega-test.com`;
+  
+  const user = await prisma.user.create({
+    data: {
+      email,
+      password: hashedPassword,
+      name: 'Test User',
+      role: 'USER'
+    }
+  });
+  
+  return user;
+}
+
+/**
+ * Cleans up a test user after tests
+ */
+export async function cleanupTestUser(prisma: PrismaClient, userId: string): Promise<void> {
+  await prisma.user.delete({
+    where: {
+      id: userId
+    }
+  });
+}

