+    expect(response.body.id).toBe(changeRequestId);
+    expect(response.body.title).toBe('Test Change Request');
+  });
+
+  it('should update a change request status', async () => {
+    const response = await request(app)
+      .patch(`/api/change-requests/${changeRequestId}/status`)
