+import { Request, Response, NextFunction } from 'express';
+import redisClient from '../config/redis';
+import { logger } from '../utils/logger';
+
+const CACHE_DURATION = 60 * 5; // 5 minutos en segundos
+
+export const cacheMiddleware = (duration = CACHE_DURATION) => {
+  return async (req: Request, res: Response, next: NextFunction) => {
+    // No cachear si es una solicitud autenticada o mÃ©todo diferente a GET
+    if (req.method !== 'GET' || req.headers.authorization) {
+      return next();
+    }
+
+    const cacheKey = `cache:${req.originalUrl}`;
+    
+    try {
+      const cachedData = await redisClient.get(cacheKey);
+      
+      if (cachedData) {
+        logger.debug(`Cache hit for ${req.originalUrl}`);
+        return res.json(JSON.parse(cachedData));
+      }
+      
+      // Interceptar el mÃ©todo res.json para guardar en cachÃ©
+      const originalJson = res.json;
+      res.json = function(body) {
+        redisClient.setex(cacheKey, duration, JSON.stringify(body));
+        return originalJson.call(this, body);
+      };
+      
+      next();
+    } catch (error) {
+      logger.error('Cache middleware error:', error);
+      next();
+    }
+  };
+};
