+import { Request, Response, NextFunction } from 'express';
+import redisClient from '../cache/redis-client';
+import { logger } from '../utils/logger';
+
+interface CacheOptions {
+  ttl?: number; // Tiempo de vida en segundos
+  keyPrefix?: string;
+}
+
+export const cacheMiddleware = (options: CacheOptions = {}) => {
+  const { ttl = 300, keyPrefix = 'yega:' } = options;
+  
+  return async (req: Request, res: Response, next: NextFunction) => {
+    // No cachear si es una solicitud POST, PUT, DELETE
+    if (req.method !== 'GET') {
+      return next();
+    }
+    
+    const cacheKey = `${keyPrefix}${req.originalUrl}`;
+    
+    try {
+      const cachedData = await redisClient.get(cacheKey);
+      
+      if (cachedData) {
+        logger.debug(`Cache hit for ${cacheKey}`);
+        return res.json(JSON.parse(cachedData));
+      }
+      
+      // Interceptar res.json para cachear la respuesta
+      const originalJson = res.json;
+      res.json = function(body) {
+        redisClient.setEx(cacheKey, ttl, JSON.stringify(body));
+        return originalJson.call(this, body);
+      };
+      
+      next();
+    } catch (error) {
+      logger.error('Cache middleware error', error);
+      next();
+    }
+  };
+};
