+import { Request, Response, NextFunction } from 'express';
+import { logger } from '../utils/logger';
+
+/**
+ * Middleware para validar la estructura del cuerpo de la solicitud de checkout
+ */
+export const validateCheckoutRequest = (req: Request, res: Response, next: NextFunction) => {
+  const { items, shippingAddress, paymentMethod } = req.body;
+  const errors = [];
+  
+  // Validar items
+  if (!items || !Array.isArray(items) || items.length === 0) {
+    errors.push('Se requiere un array de items vÃ¡lido');
+  } else {
+    // Validar estructura de cada item
+    for (const [index, item] of items.entries()) {
+      if (!item.productId) errors.push(`Item ${index}: falta productId`);
+      if (!item.quantity || item.quantity <= 0) errors.push(`Item ${index}: cantidad invÃ¡lida`);
+      if (!item.price || item.price <= 0) errors.push(`Item ${index}: precio invÃ¡lido`);
+    }
+  }
+  
+  // Validar direcciÃ³n de envÃ­o
+  if (!shippingAddress) {
+    errors.push('Se requiere una direcciÃ³n de envÃ­o');
+  }
+  
+  // Validar mÃ©todo de pago
+  if (!paymentMethod) {
+    errors.push('Se requiere un mÃ©todo de pago');
+  }
+  
+  if (errors.length > 0) {
+    logger.warn('ValidaciÃ³n de checkout fallida', { errors, body: req.body });
+    return res.status(400).json({ errors });
+  }
+  
+  next();
+};
