+import { Request, Response, NextFunction } from 'express';
+import { AnyZodObject, z } from 'zod';
+
+type ValidationSchema = {
+  body?: AnyZodObject;
+  query?: AnyZodObject;
+  params?: AnyZodObject;
+};
+
+export const validateRequest = (schema: ValidationSchema | AnyZodObject) => {
+  return async (req: Request, res: Response, next: NextFunction) => {
+    try {
+      // If schema is a Zod object, assume it's for the body
+      if ('parse' in schema) {
+        req.body = await schema.parseAsync(req.body);
+      } else {
+        // Otherwise, validate each part separately
+        if (schema.body) req.body = await schema.body.parseAsync(req.body);
+        if (schema.query) req.query = await schema.query.parseAsync(req.query);
+        if (schema.params) req.params = await schema.params.parseAsync(req.params);
+      }
+      next();
+    } catch (error) {
+      next(error);
+    }
+  };
+};
