+import { Request, Response, NextFunction } from 'express';
+import { JwtPayload } from 'jsonwebtoken';
+
+/**
+ * Middleware de autenticaci√≥n para entorno de desarrollo
+ * 
+ * Este middleware permite simular un usuario autenticado en entornos de desarrollo
+ * a√±adiendo el header X-Dev-Auth con un JSON que contiene los datos del usuario.
+ * 
+ * IMPORTANTE: Este middleware solo debe usarse en entornos de desarrollo.
+ */
+export const devAuthMiddleware = (req: Request, res: Response, next: NextFunction) => {
+  if (process.env.NODE_ENV !== 'development') {
+    return next();
+  }
+  
+  const devAuthHeader = req.header('X-Dev-Auth');
+  
+  if (devAuthHeader) {
+    try {
+      const userData = JSON.parse(devAuthHeader) as JwtPayload;
+      req.user = userData;
+    } catch (error) {
+      console.warn('Error parsing X-Dev-Auth header:', error);
+    }
+  }
+  
+  next();
+};
