interface JSONValidationResult {
  isValid: boolean
  errors: string[]
}

export function isValidJSON(data: any): JSONValidationResult {
  const errors: string[] = []

  // Validaciones b√°sicas
  if (data === undefined || data === null) {
    errors.push('JSON cannot be null or undefined')
  }

  // Verificar valores no v√°lidos
  const checkInvalidValues = (obj: any) => {
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        const value = obj[key]

        if (value === undefined) {
          errors.push(`Undefined value for key: ${key}`)
        }

        if (Number.isNaN(value)) {
          errors.push(`NaN value for key: ${key}`)
        }

        if (typeof value === 'object' && value !== null) {
          checkInvalidValues(value)
        }
      }
    }
  }

  checkInvalidValues(data)

  return {
    isValid: errors.length === 0,
    errors
  }
}
```

