export function cleanJSONResponse(data: any): any {
  if (data === null || typeof data !== 'object') return data

  if (Array.isArray(data)) {
    return data.map(cleanJSONResponse).filter(item => item !== undefined)
  }

  const cleanedObject: Record<string, any> = {}

  for (const [key, value] of Object.entries(data)) {
    if (value === undefined || value === null) continue

    if (value instanceof Date) {
      cleanedObject[key] = value.toISOString()
    } else if (typeof value === 'object') {
      const cleanedNestedValue = cleanJSONResponse(value)
      if (cleanedNestedValue !== undefined) {
        cleanedObject[key] = cleanedNestedValue
      }
    } else {
      cleanedObject[key] = value
    }
  }

  return cleanedObject
}
```

