+/**
+ * Generates a cache key based on file content hash
+ */
+export const getCacheKey = (filePath: string, prefix = ''): string => {
+  try {
+    const fileContent = fs.readFileSync(filePath);
+    const hash = crypto.createHash('md5').update(fileContent).digest('hex');
+    return `${prefix}${path.basename(filePath)}-${hash}`;
+  } catch (error) {
+    console.error(`Error generating cache key for ${filePath}:`, error);
+    // Fallback to timestamp if file can't be read
+    const stats = fs.statSync(filePath);
+    return `${prefix}${path.basename(filePath)}-${stats.mtimeMs}`;
+  }
+};
+
+/**
+ * Determines if a file or directory should be cached based on patterns
+ */
+export const shouldCache = (filePath: string, patterns: string[] = []): boolean => {
+  // Default patterns to cache
+  const defaultPatterns = [
+    'node_modules',
+    'dist',
+    'build',
+    '.next',
+    '.cache'
+  ];
+  
+  const allPatterns = [...defaultPatterns, ...patterns];
+  
+  return allPatterns.some(pattern => {
+    if (pattern.startsWith('!')) {
+      // Negative pattern (exclusion)
+      return !filePath.includes(pattern.substring(1));
+    }
+    return filePath.includes(pattern);
+  });
 };

