+import fs from 'fs';
+import path from 'path';
+import crypto from 'crypto';
+
+interface CacheConfig {
+  paths: string[];
+  cacheDir: string;
+  hashAlgorithm?: string;
+}
+
+/**
+ * Utility for selective caching based on file changes
+ */
+export class SelectiveCache {
+  private config: CacheConfig;
+
+  constructor(config: CacheConfig) {
+    this.config = {
+      hashAlgorithm: 'md5',
+      ...config
+    };
+    
+    // Ensure cache directory exists
+    if (!fs.existsSync(this.config.cacheDir)) {
+      fs.mkdirSync(this.config.cacheDir, { recursive: true });
+    }
+  }
+
+  /**
+   * Generate a cache key based on the content hash of specified paths
+   */
+  generateCacheKey(): string {
+    const hashes = this.config.paths.map(filePath => {
+      if (fs.existsSync(filePath)) {
+        const content = fs.readFileSync(filePath);
+        return crypto.createHash(this.config.hashAlgorithm!).update(content).digest('hex');
+      }
+      return '';
+    });
+    
+    return crypto.createHash(this.config.hashAlgorithm!).update(hashes.join('')).digest('hex');
+  }
+
+  /**
+   * Check if a valid cache exists for the current state
+   */
+  isCacheValid(cacheKey: string): boolean {
+    const cachePath = path.join(this.config.cacheDir, cacheKey);
+    return fs.existsSync(cachePath);
+  }
+}","note":"Crear una nueva utilidad de cache selectivo que genera claves de cache basadas en el contenido de archivos espec√≠ficos"},{"path":"src/deploy/pipeline.ts","patch":"--- a/src/deploy/pipeline.ts
