+import { Request, Response, NextFunction } from 'express';
+import { logger } from '../utils/logger';
+
+/**
+ * Middleware de autenticaci√≥n para desarrollo
+ * 
+ * Este middleware permite simular un usuario autenticado en entornos de desarrollo
+ * facilitando pruebas sin necesidad de tokens JWT v√°lidos.
+ * 
