+export const devAuthMiddleware = (req: Request, res: Response, next: NextFunction) => {
+  // Solo activar en entorno de desarrollo
+  if (process.env.NODE_ENV !== 'development') {
+    return next();
+  }
+  
+  // Verificar si se env√≠a el header especial de desarrollo
+  const devAuthHeader = req.headers['x-dev-auth-user'];
+  
+  if (devAuthHeader) {
+    try {
+      // Simular usuario autenticado para desarrollo
+      req.user = JSON.parse(devAuthHeader as string);
+      logger.warn('Usando autenticaci√≥n de desarrollo para: ' + req.user.email);
+    } catch (error) {
+      logger.error('Error al parsear x-dev-auth-user header:', error);
+    }
+  }
+  
+  next();
+};
