+  hashAlgorithm: 'md5',
+  logLevel: 'info'
+};
+
+/**
+ * Generates a cache key based on the content of files matching the patterns
+ */
+export function getCacheKey(basePath: string, options: CacheOptions = {}): string {
+  const opts = { ...defaultOptions, ...options };
+  const { includePatterns, excludePatterns, hashAlgorithm, logLevel } = opts;
+  
+  const hash = crypto.createHash(hashAlgorithm);
+  const files = getMatchingFiles(basePath, includePatterns, excludePatterns);
+  
+  if (logLevel === 'verbose') {
+    logger.info(`Generating cache key from ${files.length} files`);
+  }
+  
+  for (const file of files) {
+    try {
+      const content = fs.readFileSync(file, 'utf8');
+      hash.update(`${file}:${content}`);
